<?php

use Illuminate\Support\MessageBag;

class Category extends BaseModel {
    use hasImage;

    /**
     * The database collection
     *
     * @var string
     */
    protected $collection = 'categories';

    /**
     * Validation rules
     *
     * @var array
     */
    public static $rules = array(
        'name' => 'required',
    );

    /**
     * Attributes that will be generated by FactoryMuff
     */
    public static $factory = array(
        'name' => 'string',
        'image' => 'categoryimage.jpg',
        'parents' => array(),
        'kind' => 'leaf',
        'shortDesc' => 'text',
        'description' => 'text',
        'template' => 'default',
        'productTemplate' => 'default'
    );

    protected $guarded = array(
        'image_file',
        '_id',
    );

    /**
     * Path where category images will be stored
     *
     * @var string
     */
    private $images_path = '../public/assets/img/categories';

    /**
     * Reference to parent
     */
    public function parent()
    {
        return $this->belongsToMany('Category','parents');
    }

    /**
     * Return all the parents
     *
     */
    public function parents()
    {
        return $this->parent()->getQuery()->get();
    }

    /**
     * Return all the childs
     *
     */
    public function childs()
    {
        return Category::where('_id',['$in'=>$this->category_id])->get();
    }

    /**
     * Verify if the model is valid
     *
     * @return bool
     */
    public function isValid()
    {
        $valid = parent::isValid();

        if( $valid )
        {
            // does a category with the same name and with different _id exists?
            $exists = Category::where(['name'=>$this->name, '_id'=>['$ne'=>$this->_id]])->count();

            if( $exists )
            {
                $this->errors = new MessageBag(['JÃ¡ existe uma categoria com esse nome']);
                return false;
            }
            else
            {
                return true;
            }
        }

        return false;
    }

}
